import React from "react";
import {Link} from "react-router-dom";

export default function MailCard(props) {
    let path = "/mail/" + props.mailid;
    return (
        <Link to={path}>
            <div>
                {props.address}
                {props.datetime}
                {props.theme}
                <b>{props.text}</b>
            </div>
        </Link>
    )
}
