import React from "react";
import Header from '../components/header/Header.jsx';
import Categories from '../components/categories/Categories.jsx';
import Tags from '../components/tags/Tags.jsx';
import MailList from "../components/mail_list/MailList.jsx";
import MailContent from "../components/mail_list/MailContent.jsx";
import { list } from "../components/mail_list/MailList.jsx";
import {useParams }  from "react-router-dom";

function Main() {
	const {id} = useParams();
	let userId = parseInt(id);
	console.log(userId)
    return (
        <div className="App">
    	<Header></Header>
    	<div className="mainInterface">
        	<div className="catsAndTags">
        		<Categories title="Папки"></Categories>
        		<Tags></Tags>
        	</div>
        	<div>
				<MailList/>
			</div>
			<div>
				<MailContent key={list[userId].id} content={list[0].content}/>
			</div>
    	</div>
    </div>
    )
}

export default Main;
